sp_widget.client_script:
api.controller = function OPBreadcrumbsCtrl(spUtil, $rootScope, $scope) {
    var c = this;
	var thisPage = {
		url: '#',
		label: $scope.page.title
	}

    if (!c.options.use_fixed_breadcrumbs) {
        var deregister = $rootScope.$on("sp.update.breadcrumbs", function (e, list) {
            c.breadcrumbs = list;
        });

        $scope.$on('$destroy', function () {
            deregister();
        });

		if (!c.breadcrumbs || c.breadcrumbs.length < 1) {
			c.breadcrumbs = [thisPage];
		}

    } else {
        c.breadcrumbs = c.options.fixed_breadcrumbs;
    }
};

sp_widget.css:
$wds-op-breadcrumbs-linkHoverColor: darken($link-color,10%) !default;
$wds-op-breadcrumbs-linkColor: $link-color !default;
$wds-op-breadcrumbs-dividerColor: #323232 !default;
$wds-op-breadcrumbs-textColor: $text-muted !default;

.op_breadcrumbs {
    font-size: 18px;
 		padding-top: 5px;
  	padding-bottom: 5px;
    letter-spacing: -0.26px;
    cursor: default;

    a {
        color: $wds-op-breadcrumbs-linkColor;

        &:hover {
            color: $wds-op-breadcrumbs-linkHoverColor;
        }
    }

    ul {
        list-style: none;
        margin: 0;
        padding: 0;
        display: flex;
    }

    li {

        position: relative;
        padding-left: 10px;

        &:first-of-type {
            padding-left: 0;
        }

        &:last-of-type {
            color: $wds-op-breadcrumbs-textColor;
        }

        &:not(:last-of-type) {

            margin-right: 14px;

            &:after {
                content: "/";
                position: absolute;
                margin-left: 10px;
                top: 0;
                right: -15px;
                color: $wds-op-breadcrumbs-dividerColor;
            }
        }
    }
}
