sp_widget.link:
function link(scope, element, attrs, controller) {
	$('.count').each(function () {
		alert($(this).text());
		$(this).prop('Counter',0).animate({
			Counter: $(this).text()
		}, {
			duration: 1500,
			easing: 'linear',
			step: function (now) {
				$(this).text(Math.ceil(now));
			}
		});
	});
}
